  @override
  Widget build(BuildContext context) {
    return Container(
      child: ListView.builder(
        physics: BouncingScrollPhysics(),
        itemCount: data.length,
        itemBuilder: (ctx, index) {
          InfoModel infoModel;
          return ListVidget(data[index]);
        },
      ),
    );
  }

  Widget ListVidget(InfoModel data) {
    return Container(
      decoration: decoration1(Colors.grey.withOpacity(0.2)),
      padding: EdgeInsets.all(10),
      margin: EdgeInsets.all(5),
      child: ExpandablePanel(
        header: Text(
          data.name,
          style: largeText(Colors.black, bold: true),
        ),
        collapsed: SizedBox.shrink(),
        expanded: Column(
          children: [
            Image.asset(
              data.image,
              fit: BoxFit.fitWidth,
            ),
            Text(
              data.content,
              textAlign: TextAlign.justify,
              softWrap: true,
              style: smallText(
                Colors.black,
              ),
            ),
          ],
        ),
      ),
    );
  }
}

class InfoModel {
  String image;
  String name;
  String content;

  InfoModel(this.image, this.name, this.content);
}
